import React from "react";
// import "../../lib/js/filter";
import "./style.css";

const Filters = props => {
  const { handleFilter } = props;

  let sectionstyle = {
    height: "80vh"
  };
  let block = {
    border: "1px solid black",
    display: "block"
  };

  return (
    <section className="col-md-3 bg-white btn-cont-hide " style={sectionstyle}>
      <div className="row ">
        <div
          className="col-sm-12 sticky fixedleft btn-cont-hide "
          id="myBtnContainer"
        >
          <h1>Soccer Jerseys</h1>
          <h3>Filters</h3>
          <button
            style={block}
            value="seriea"
            className="btn btn-white"
            onClick={handleFilter}
          >
            Serie A
          </button>

          <button
            style={block}
            value="premier"
            className="btn btn-white"
            onClick={handleFilter}
          >
            Premier
          </button>
          <button
            style={block}
            value="laliga"
            className="btn btn-white"
            onClick={handleFilter}
          >
            La Liga
          </button>
          <button
            style={block}
            value="ligamx"
            className="btn btn-white"
            onClick={handleFilter}
          >
            Liga Mx
          </button>
          <button
            style={block}
            value="national"
            className="btn btn-white"
            onClick={handleFilter}
          >
            National
          </button>
          <button
            style={block}
            value="bundesliga"
            className="btn btn-white"
            onClick={handleFilter}
          >
            Bundesliga
          </button>
          <button
            style={block}
            value="all"
            className="btn btn-white"
            onClick={handleFilter}
          >
            Show all
          </button>
        </div>
      </div>
      <div class="bg-dark filters filter-small">
        <div class="dropdown">
          <button
            type="button"
            class="btn btn-lg bg-dark btn-dark  dropdown-toggle"
            data-toggle="dropdown"
          >
            <h2>Filters</h2>
          </button>
          <div class="dropdown-menu bg-dark">
            <button
              style={block}
              value="seriea"
              className="btn btn-white"
              onClick={handleFilter}
            >
              Serie A
            </button>

            <button
              style={block}
              value="premier"
              className="btn btn-white"
              onClick={handleFilter}
            >
              Premier
            </button>
            <button
              style={block}
              value="laliga"
              className="btn btn-white"
              onClick={handleFilter}
            >
              La Liga
            </button>
            <button
              style={block}
              value="ligamx"
              className="btn btn-white"
              onClick={handleFilter}
            >
              Liga Mx
            </button>
            <button
              style={block}
              value="national"
              className="btn btn-white"
              onClick={handleFilter}
            >
              National
            </button>
            <button
              style={block}
              value="bundesliga"
              className="btn btn-white"
              onClick={handleFilter}
            >
              Bundesliga
            </button>
            <button
              style={block}
              value="all"
              className="btn btn-white"
              onClick={handleFilter}
            >
              Show all
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Filters;
