import React from "react";
// import "../../lib/js/filter";
import "./style.css";

function Filters() {
  let sectionstyle = {
    height: "80vh"
  };
  let block = {
    display: "block"
  };
  let inputstyle = {
    border: "0",
    color: "black",
    fontweight: "bold"
  };
  let sliderdiv = {
    width: "270px"
  };
  return (
    <section className="col-md-3 bg-white btn-cont-hide " style={sectionstyle}>
      <div className="row ">
        <div
          className="col-sm-12 sticky fixedleft btn-cont-hide "
          id="myBtnContainer"
        >
          <h1>Soccer Jerseys</h1>
          <button style={block} className="btn btn-white">
            Serie A
          </button>

          <button style={block} className="btn btn-white">
            Premier
          </button>
          <button style={block} className="btn btn-white">
            La Liga
          </button>
          <button style={block} className="btn btn-white">
            Liga Mx
          </button>
          <button style={block} className="btn btn-white">
            National
          </button>
          <button style={block} className="btn btn-white">
            Bundesliga
          </button>
          <button style={block} className="btn btn-white">
            Show all
          </button>
          <div className="demo">
            <p>
              <label for="amount">Price range:</label>
              <input type="text" id="amount" style={inputstyle} />
            </p>

            <div style={sliderdiv} id="slider-range" />
            <input type="range" />
            <hr />
            <h2>Colors</h2>
            <button className="btn btn-warning button--left">Yellow</button>
            <button className="btn btn-success button--left">Green</button>
            <br />
            <button className="btn btn-primary button--left">Blue</button>
            <button className="btn btn-dark button--left">Black</button>
            <br />
            <button className="btn btn-danger button--left">Red</button>
            <button className="btn button--left">White</button>

            <br />
          </div>
        </div>
      </div>
    </section>
  );
}

export default Filters;
